import React from "react";
import { useRecoilValue } from "recoil";
import styled from "styled-components";

import { isWideNavState } from "recoil/atoms/mainAtom";
import NavOpen from "./Nav.Open";
import NavClosed from "./Nav.Closed";

const StyledNav = styled.nav`
    position: fixed;
    z-index: 9;

    height: 100%;
    background: #fff;

    ${props => {
        if (props.wide)
            return ` width: 240px; margin-top: 0; `
        else 
            return "margin-top: 56px;"
    }}
`;

const Nav = () => {

    // 네비바 열림/닫힘 여부
    const isWideNav = useRecoilValue(isWideNavState);

    return (
        <StyledNav wide={isWideNav}>
            {
                !isWideNav
                    ? <NavClosed />
                    : <NavOpen />
            }
        </StyledNav>
    );
}

export default Nav;
