import React, { useState } from "react";
import { useSelector } from "react-redux";

import Header from "@Header/Header";
import Nav from "@Nav/Nav";
import MainHome from "@Main/MainHome";
import MainShorts from "@Main/MainShorts";
import MainSubscribe from "@Main/MainSubscribe";
import MainLibrary from "@Main/MainLibrary";

const App = () => {

    // 네비바 열림/닫힘 여부
    // const [isOpen, setIsOpen] = useState(false);
    const isOpen = useSelector(state => state.isOpen);

    // 선택 메뉴
    const [selectedMenu, setSelectedMenu] = React.useState(0);

    // 첫 로드 시 스크롤 최상단 위치
    React.useEffect(() => {
        window.scrollTo(0, 0);
    }, [selectedMenu]);
    
    return (
        <React.Fragment>
            <Header />
            <Nav isOpen={isOpen} selectedMenu={selectedMenu} setSelectedMenu={setSelectedMenu} />
            {
                selectedMenu === 0 && <MainHome />
            }
            {
                selectedMenu === 1 && <MainShorts />
            }
            {
                selectedMenu === 2 && <MainSubscribe />
            }
            {
                selectedMenu === 3 && <MainLibrary />
            }
        </React.Fragment>
    );
}

export default App;
