import { createStore } from "redux";

const initState = {
    "isOpen": false,
    "selectedMenu": 0,
}

const reducer = ( state = initState, action ) => {

    switch(action.type) {
        case "TOGGLE_MENU":
            return {
                ...state,
                "isOpen": !state
            }
        default:
            return state;
    }
}

const store = createStore(reducer);

export default store;
