import styled from "styled-components";

export const AlignDiv = styled.div`
    display: flex;
    ${ props => setAlignment(props.flex) }
`;

const setAlignment = (type) => {
    switch (type) {
        case "col-center":
            return `
                align-items: center;
            `;
        case "row-center":
            return `
                justify-content: center;
            `;
        case "row-between":
            return `
                justify-content: space-between;
            `;
        case "center":
            return `
                justify-content: center;
                align-items: center;
            `;
        }
}

const setVerticalAlign = (col) => {
    switch (col) {
        case "center":
            return `
                align-items: center;
            `;
            
    }

}

const setHorizontalAlign = (row) => {
    switch (row) {
        case "center":
            return `
                justify-content: center;
            `;
        case "between":
            return `
                justify-content: space-between;
            `;
    }
}
