import React from "react";
import styled, { css } from "styled-components";

import { isOpenNavState } from "recoil/atoms/mainAtom";

const MenuItem = styled.li`
    font-size: 11px;
    text-align: center;
    border-radius: 7px;
    cursor: pointer;
    ${props => {
        console.log(props)
        props.open ? css`
            width: 100%;
            height: 82px;
            padding: 13px 0;

            display: flex;
            flex-direction: column;
            justify-content: space-evenly;
            align-items: center;

            ${MenuName} {
                font-size: 15px;
                text-align: start;
            }
        `
        : css`
            width: 88%;
            height: 48px;
            display: grid;
            grid-template-columns: 1fr 3fr;
            grid-gap: 14px;
        `}
    }

    ${props => props.active && `background: #F2F2F2;`}

    &:hover {
        background: #F2F2F2;
    }
`;

const MenuName = styled.span``;

const NavItem = ((props) => {
    
    const {item: {id, name}, isSelected} = props;

    return (
        <MenuItem key={id} open={isOpenNavState} active={isSelected}>
            <div>
                <img className={`i-${id}`} src={require(`assets/${id}.png`)} alt={id} />
            </div>
            <MenuName>{name}</MenuName>
        </MenuItem>
    )
})

export default NavItem;
