const toggleMenuButton = () => {
    return {
        "type": "TOGGLE_MENU"
    }
}

const setSelectedMenu = (idx) => {
    return {
        "type": "SET_SELECTED_MENU",
        "payload": idx
    }
}

const setSelectedKeyword = (idx) => {
    return {
        "type": "SET_SELECTED_KEYWORD",
        "payload": idx
    }
}

export { toggleMenuButton, setSelectedMenu, setSelectedKeyword }
